import React, { useState } from "react";
import { useGoogleLogin } from "@react-oauth/google";
import axios from "axios";
import ClassroomCalendar from "./ClassroomCalendar";

const App = () => {
  const [events, setEvents] = useState([]);

  const fetchClassroomData = async (token) => {
    try {
      const response = await axios.get("https://classroom.googleapis.com/v1/courses", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Fetched courses:", response.data);
      // Process classroom data...
    } catch (error) {
      console.error("Error fetching classroom data:", error);
    }
  };

  const login = useGoogleLogin({
    onSuccess: async (tokenResponse) => {
      console.log("Login Successful:", tokenResponse);
      const token = tokenResponse.access_token;

      // Debug Token Expiration
      const tokenIssuedAt = new Date();
      const expiresIn = tokenResponse.expires_in * 1000;
      const expirationTime = new Date(tokenIssuedAt.getTime() + expiresIn);
      console.log("Token issued at:", tokenIssuedAt);
      console.log("Token expires at:", expirationTime);

      fetchClassroomData(token);
    },
    onError: (error) => console.error("Login failed:", error),
    scope: "https://www.googleapis.com/auth/classroom.courses.readonly https://www.googleapis.com/auth/classroom.coursework.me.readonly",
    access_type: "offline", // Enables refresh token
  });

  const logout = () => {
    console.log("Logging out...");
    window.location.reload();
  };

  return (
    <div>
      <h1>Classroom Calendar</h1>
      {!events.length ? (
        <>
          <button onClick={() => login()}>Login with Google</button>
          <p>No events to display</p>
        </>
      ) : (
        <>
          <button onClick={logout}>Logout</button>
          <h2>Classroom Calendar</h2>
          <ClassroomCalendar events={events} />
        </>
      )}
    </div>
  );
};

export default App;
