import React, { useState } from "react";
import { useGoogleLogin } from "@react-oauth/google";
import axios from "axios";
import ClassroomCalendar from "./ClassroomCalendar";

const App = () => {
  const [events, setEvents] = useState([]);

  // Fetch Classroom Data (Courses and Assignments)
  const fetchClassroomData = async (token) => {
    try {
      console.log("Fetching classroom data with token:", token);

      // Fetch courses
      const coursesResponse = await axios.get(
        "https://classroom.googleapis.com/v1/courses",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      console.log("Fetched Courses Response:", coursesResponse.data);

      const courses = coursesResponse.data.courses || [];
      if (courses.length === 0) {
        console.warn("No courses found for the logged-in account.");
        setEvents([]); // No events to display
        return;
      }

      // Fetch coursework for each course
      const allEvents = [];
      for (const course of courses) {
        try {
          const courseworkResponse = await axios.get(
            `https://classroom.googleapis.com/v1/courses/${course.id}/courseWork`,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          console.log(
            `Course ID: ${course.id}, Assignments:`,
            courseworkResponse.data
          );

          const coursework = courseworkResponse.data.courseWork || [];
          coursework.forEach((assignment) => {
            if (assignment.dueDate) {
              const { dueDate, dueTime } = assignment;
              allEvents.push({
                title: assignment.title,
                start: new Date(
                  dueDate.year,
                  dueDate.month - 1,
                  dueDate.day,
                  dueTime?.hours || 0,
                  dueTime?.minutes || 0
                ),
                end: new Date(
                  dueDate.year,
                  dueDate.month - 1,
                  dueDate.day,
                  dueTime?.hours || 23,
                  dueTime?.minutes || 59
                ),
              });
            } else {
              allEvents.push({
                title: assignment.title,
                start: null,
                end: null,
              });
            }
          });
        } catch (error) {
          console.error(`Error fetching coursework for course ID: ${course.id}`, error);
        }
      }

      setEvents(allEvents); // Update events with all assignments
      console.log("Final Events:", allEvents);
    } catch (error) {
      console.error("Error fetching classroom data:", error.response || error);
    }
  };

  // Google Login
  const login = useGoogleLogin({
    onSuccess: async (tokenResponse) => {
      console.log("Login Successful:", tokenResponse);
      const token = tokenResponse.access_token;

      // Debug Token Expiration
      const tokenIssuedAt = new Date();
      const expiresIn = tokenResponse.expires_in * 1000; // Convert seconds to milliseconds
      const expirationTime = new Date(tokenIssuedAt.getTime() + expiresIn);
      console.log("Token issued at:", tokenIssuedAt);
      console.log("Token expires at:", expirationTime);

      fetchClassroomData(token);
    },
    onError: (error) => console.error("Login failed:", error),
    scope: "https://www.googleapis.com/auth/classroom.courses.readonly https://www.googleapis.com/auth/classroom.coursework.me.readonly",
  });

  // Logout (Clear State)
  const logout = () => {
    console.log("Logging out...");
    window.location.reload(); // Reload the app to clear token and state
  };

  return (
    <div>
      <h1>Classroom Calendar</h1>
      {!events.length ? (
        <>
          <button onClick={() => login()}>Login with Google</button>
          <p>No events to display</p>
        </>
      ) : (
        <>
          <button onClick={logout}>Logout</button>
          <h2>Your Assignments</h2>
          <ClassroomCalendar events={events} />
        </>
      )}
    </div>
  );
};

export default App;
