import React, { useState } from "react";
import { useGoogleLogin } from "@react-oauth/google";
import axios from "axios";
import ClassroomCalendar from "./ClassroomCalendar";

const App = () => {
  const [events, setEvents] = useState([]);

  const login = useGoogleLogin({
    onSuccess: async (tokenResponse) => {
      console.log("Login successful, token:", tokenResponse);

      const token = tokenResponse.access_token;

      try {
        // Fetch courses from Google Classroom API
        const coursesResponse = await axios.get(
          "https://classroom.googleapis.com/v1/courses",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        console.log("Courses fetched:", coursesResponse.data);
        const courses = coursesResponse.data.courses || [];
        const assignments = [];

        // Fetch assignments for each course
        for (const course of courses) {
          try {
            const courseworkResponse = await axios.get(
              `https://classroom.googleapis.com/v1/courses/${course.id}/courseWork`,
              {
                headers: {
                  Authorization: `Bearer ${token}`,
                },
              }
            );

            console.log(`Course ID: ${course.id}, Assignments:`, courseworkResponse.data);
            const coursework = courseworkResponse.data.courseWork || [];

            coursework.forEach((work) => {
              console.log("Processing assignment:", work);

              // Check if the assignment has a dueDate
              if (work.dueDate) {
                const { year, month, day } = work.dueDate;
                const hours = work.dueTime?.hours || 23; // Default to 11:00 PM if no dueTime
                const minutes = work.dueTime?.minutes || 59; // Default to 59 minutes

                assignments.push({
                  title: work.title,
                  start: new Date(year, month - 1, day, hours, minutes), // JS months are 0-based
                  end: new Date(year, month - 1, day, hours, minutes + 30), // Default duration: 30 mins
                  topicId: work.topicId || null, // Include topicId for debugging or later use
                });
              } else {
                console.warn("Skipping assignment without dueDate:", work.title, work.topicId ? `(Topic: ${work.topicId})` : "");
              }
            });
          } catch (error) {
            console.error(`Error fetching coursework for course ID: ${course.id}`, error);
          }
        }

        console.log("Final events:", assignments);
        setEvents(assignments);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    },
    onError: (error) => console.error("Login failed:", error),
    scope: "https://www.googleapis.com/auth/classroom.courses.readonly https://www.googleapis.com/auth/classroom.coursework.me",
  });

  return (
    <div>
      <h1>Classroom Calendar</h1>
      {!events.length ? (
        <>
          <button onClick={() => login()}>Login with Google</button>
          <p>No events to display</p>
        </>
      ) : (
        <>
          <ClassroomCalendar events={events} />
           {/* Debugging: Shows events as JSON */}
        </>
      )}
    </div>
  );
};

export default App;
